    <div class="title">垃圾分类知识竞赛</div>
    <img class="pimg" src="http://localhost/static/challenge/bg4.gif" alt="">
    <button open-type="getUserInfo" @getuserinfo="handleGetUserInfo"  class="btn">开始体验</button>
  </div>
</template>

<script>

export default{
    data(){
        return {
          userInfo:""
        }
    },
    methods: {
      handleGetUserInfo(ev){
        if(ev.mp.detail.userInfo){
          this.userInfo = ev.mp.detail.userInfo

          wx.switchTab({
            url:"../index/main"
          })
        }
      }
    },
    mounted(){
    },
    onLoad(){
      wx.getUserInfo({
        success:(res)=>{
          wx.switchTab({
            url:"../index/main"
          })
        }
      })
    }
}
</script>

